package com.kodnest.patterns;

public class Pattern1Usingdowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;//initialization
		do
		{
			System.out.print("*");//Instructions given
			i++;//Incrementing i value
		}
       while(i<=5);//checking condition
	}

}
